import Queue from 'bull';
import dbClient from './utils/db';
import fs from 'fs';
import imageThumbnail from 'image-thumbnail';
import path from 'path';

// Create the fileQueue
const fileQueue = new Queue('fileQueue');

// Process jobs from the queue
fileQueue.process(async (job) => {
  const { userId, fileId } = job.data;

  // Check for missing fileId or userId
  if (!fileId) {
    throw new Error('Missing fileId');
  }

  if (!userId) {
    throw new Error('Missing userId');
  }

  // Find the file document in the database
  const file = await dbClient.db.collection('files').findOne({
    _id: new dbClient.ObjectId(fileId),
    userId: new dbClient.ObjectId(userId),
  });

  if (!file) {
    throw new Error('File not found');
  }

  // Generate thumbnails if the file is of type image
  const filePath = file.localPath;
  if (file.type !== 'image' || !fs.existsSync(filePath)) {
    throw new Error('Invalid file type or file does not exist');
  }

  try {
    // Generate thumbnails with sizes 500, 250, and 100
    const sizes = [500, 250, 100];
    for (const size of sizes) {
      const thumbnail = await imageThumbnail(filePath, { width: size });
      const thumbnailPath = `${filePath}_${size}`;
      fs.writeFileSync(thumbnailPath, thumbnail);
    }
  } catch (error) {
    console.error('Error generating thumbnails:', error);
    throw new Error('Thumbnail generation failed');
  }
});

export { fileQueue };

